import React from "react"
import Img from "./Img"
import Form from "./Form"

class App extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            topText: "Hello",
            bottomText: "There"
        }

        ;(this.handleSubmit = (event) => {
            event.preventDefault();
            console.log("Submitted!")
            console.log(event.target.topText.value, event.target.bottomText.value)
        }).bind(this)
    }

    render(props){
        //https://api.imgflip.com/get_memes
        return(
            <div id='app'>
                <Form handleSubmit={this.handleSubmit}/>
                <Img image="http://i.imgflip.com/1bij.jpg" topText={this.state.topText} bottomText={this.state.bottomText}/>
            </div>
        )
    }
}

export default App;
