import React from "react"
import GridDot from "./GridDot"

class Grid extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            selectorIndex: 7
        }
    }

    render(){
        console.log(this.dotArray)
        this.dotArray[this.selectorIndex].select()

        return(
            <div id="grid">
                {this.dotArray}
            </div>
        )
    }


    dotArray = Array(64).fill(null).map((ele, i) => <GridDot temp={i+1}/>)
}

export default Grid
