import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let  nIndex = 0;

class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value: null
        }

    }

    render() {

        console.log("squre made")
        return (
            <button className="square">
                {this.state.value}
            </button>
          )
    }




  }

class Board extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            squares: Array(9).fill(null)
        }
    }

    handleClick(i){
        //toggles box between 'X' 'O' and ' '
        console.log("box clicked")
        const squares = this.state.squares.slice();
        squares[i] = 'X';
        this.setState({squares: squares});
        // let squares = this.state.squares.slice()

        // switch(squares[i]){
        //     case 'X':
        //         squares[i] = 'O'
        //         break;
        //     case 'O':
        //         squares[i] = "";
        //         break;
        //     default:
        //         squares[i] = "X";
        // } 

        // this.setState({squares: squares});
    }

    renderSquare(i) {
        return (
          <Square
            value={this.state.squares[i]}
            onClick={() => this.handleClick(i)}
          />
        );
      }

    n() {

        let returnMe = nIndex;
        nIndex++;
        return returnMe;
    }

    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>

          <div className="board-row">
            {this.renderSquare(1)}
            {this.renderSquare(1)}
            {this.renderSquare(1)}
        </div>
        <div className="board-row">
            {this.renderSquare(1)}
            {this.renderSquare(1)}
            {this.renderSquare(1)}
        </div>
        <div className="board-row">
            {this.renderSquare(1)}
            {this.renderSquare(1)}
            {this.renderSquare(1)}
        </div>
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
