import React from "react"
import ReactDOM from "react-dom"
import LightGrid from "./components/LightGrid"
import ButtonContainer from "./components/ButtonContainer"
import "./styles.css"
// import url("https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.11.2/css/all.css")

class App extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            gridColors: ["red", "red", "red", "red"]
        }
    }

    render(){
        return(
            <div id="app">
                <link href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.11.2/css/all.css" rel="stylesheet"></link>
                <LightGrid/>
                <ButtonContainer actions={this.actions}/>
            </div>
        )
    }


    actions = {
        "mask": ()=>{console.log("mask")},
        "spider": ()=>{console.log("spider")},
        "camera": ()=>{console.log("camera")},
        "kiwi": ()=>{console.log("kiwi")}, 
        "lemon": ()=>{console.log("lemon")},
        "death": ()=>{console.log("death")},
        "d20": ()=>{console.log("d20")},
    }
}

export default App;
