import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let  nIndex = 0;

class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value: null
        }

    }

    render() {
        return (
            <button className="square">
                {this.state.value}
            </button>
          )
    }




  }

