import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let  nIndex = 0;

class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value: null
        }

    }

    render() {
        return (
            <button className="square" 
                onClick={() => this.callThisWhenBoxClicked()}
            >
                {this.state.value}
            </button>
          )
    }

    callThisWhenBoxClicked(){
        //CURRENTLY:  toggles box between 'X' 'O' and ' '

        let nextState = 'X';

        switch(this.state.value){
            case 'X':
                nextState = 'O'
                break;
            case 'O':
                nextState = "";
                break;
            default:
                nextState = "X";
        } 

        this.setState({value: nextState});
    }


  }

