import React from "react"
import UltimateContext from "./UltimateTheme"

export default function Button(props){
    return (
        <UltimateContext.Consumer>
            {(arg) => {
                console.log(arg.text)
                return (
                    <button onClick={arg.toggle}> {arg.text} </button>
                )
            }}
        </UltimateContext.Consumer>
    )
}
