import React from "react"
import GridDot from "./GridDot"

class Grid extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            selectorIndex: 0

        }
    }

    render(){
        gridDots = [].length = 12;

        return(
            <div className="grid">
                {gridDots.map(() => <GridDot/>)}
            </div>
        )
    }
}

export default Grid
