import React from "react"
import ReactDOM from "react-dom"
import "./styles.css"
import { SSL_OP_SSLEAY_080_CLIENT_DH_BUG } from "constants";
class App extends React.Component{
    constructor(props){
        super(props)

        this.state = {
        }

        ;(this.handleSubmit = (event) => {
            event.preventDefault();
            console.log(event);
            event.persist();
            //target[].name target[].value
            let newState = {};
            for(let i = 0; i < event.target.length; i++){
                newState[event.target[i].name] = event.target[i].value
            }
            console.log(newState)
            this.setState(newState)
            console.log(this.state)
        }).bind(this)

    }

    render(props){

        return (
            <div id="app">
                <form name="form" onSubmit={this.handleSubmit}>
                    <input type="text" name="fName"  placeholder="first name"></input>
                    <input type="text" name="lName"  placeholder="last name"></input>
                    <input type="text" name="email"  placeholder="email"></input>
                    <input type="text" name="birthplace"  placeholder="birthplace"></input>
                    <input type="text" name="phone"  placeholder="phone number"></input>
                    <input type="text" name="food"  placeholder="favorite food"></input>
                    <textarea name="about"></textarea>
                    <input type="submit" value="Submit"></input>
                </form>
                <div id="badge">
                    <div id="fName"> {this.state.fName || "-"}  </div>
                    <div id="lName"> {this.state.fName || "-"} </div>
                    <div id="email"> {this.state.fName || "-"} </div>
                    <div id="birthplace"> {this.state.fName || "-"} </div>
                    <div id="phone"> {this.state.fName || "-"}</div>
                    <div id="food"> {this.state.fName || "-"} </div>
                    <div id="about"> {this.state.fName || "-"} </div>
                </div>
            </div>
        )
    }
}

export default App;
