import React, {useState, useContext} from "react"
import UltimateContext, {values} from "./UltimateTheme"
import Cube from "./Cube"

export default function App(){

    const [value, setValue] = useState({
        text: UltimateContext.theme,
        toggle: () => {
            setValue({
                text: values[Math.floor(Math.random()*values.length)]
            })
            console.log(value)
            console.log(text)
        }
    })
    const toggle = useState()


    return (
        <UltimateContext.Provider value={value}>
            <Cube/>
        </UltimateContext.Provider>
    )
}
