import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

let  nIndex = 0;

class Square extends React.Component {
    constructor(props){
        super(props);
        this.state = {
            value: null
        }

    }

    render() {
        return (
            <button className="square" 
                onClick={() => this.dynamicallySetNextState()}
            >
            {this.state.value}
            </button>
          )
    }

    dynamicallyGetNextState(){
        let returnMe = 'X'

        switch(this.state.value){
            case 'X':
                returnMe = 'O'
                break;
            case 'O':
                returnMe = "";
                break;
        } 

        return returnMe;
    }


  }

class BoardRow extends React.Component {
    renderSquare(i) {
        return <Square value={i}/>;
    }

    render() {

        let prnt = this.props.prnt

       return(
            <div className="board-row">
                {this.renderSquare(prnt.n())}
                {this.renderSquare(prnt.n())}
                {this.renderSquare(prnt.n())}
            </div>
       ) 
    }
}

class Board extends React.Component {

    n() {
        let returnMe = nIndex;
        nIndex++;
        return returnMe;
    }

    render() {
      const status = 'Next player: X';

      return (
        <div>
          <div className="status">{status}</div>
          <BoardRow prnt={this} />
          <BoardRow prnt={this} />
          <BoardRow prnt={this} />
        </div>
      );
    }
  }

  class Game extends React.Component {
    render() {
      return (
        <div className="game">
          <div className="game-board">
            <Board />
          </div>
          <div className="game-info">
            <div>{/* status */}</div>
            <ol>{/* TODO */}</ol>
          </div>
        </div>
      );
    }
  }

  // ========================================

  ReactDOM.render(
    <Game />,
    document.getElementById('root')
  );
