import React from "react"
import ReactDOM from "react-dom"
import LightGrid from "./components/LightGrid"
import ButtonContainer from "./components/ButtonContainer"
import "./styles.css"
// import url("https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.11.2/css/all.css")

class App extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            gridColors: ["red", "red", "red", "red"],
            selectorIndex: 0,
            direction: 1,
        }

        for (let [key, value] of Object.entries(this.actions)) {
            this.actions[key].bind(this)
        }

        for (let [key, value] of Object.entries(this.internal)) {
            this.internal[key].bind(this)
        }
    }

    render(){
        return(
            <div id="app">
                <link href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.11.2/css/all.css" rel="stylesheet"></link>
                <LightGrid/>
                <ButtonContainer actions={this.actions}/>
            </div>
        )
    }


    actions = {
        "mask": ()=>{ console.log("mask")/*make active + opposite black, if either black then both white*/},
        "spider": ()=>{ /*Random color, switch direction*/},
        "camera": ()=>{console.log(/*opposite + previous shift back one color */)},
        "kiwi": ()=>{console.log(/*adjacent green */)}, 
        "lemon": ()=>{console.log(/*yellow, switch to opposite */)},
        "death": ()=>{console.log(/*make black, if black adjacent black*/)},
        "d20": ()=>{console.log(/*all random color, switch to opposite */)},
    }

    internal = {
        advance: ()=>{
            let newIndex = this.state.selectorIndex + this.state.direction;

            if(newIndex >= this.state.gridColors.length)
                newIndex = 0; //loop back to first index

            this.setState({
                selectorIndex: newIndex
            })
        },

        reverse: ()=>{
            let newDirection = -1; //1 is forwards, -1 is backwards

            if(this.state.direction === -1)
                newDirection = 1;

            this.setState({
                direction: newDirection
            })

        },
        setColor: (which, newColor)=>{

            let newColors = [...this.state.gridColors]

            newColors[which] = this.colorOptions[newColor]

            this.setState(
                {
                    gridColors: newColors
                }
            )
        },
        getColor: (which)=>{},
        goToOpposite: ()=>{},
        generateRandomColor: ()=>{},
    }

    colorOptions = [
        "lightblue",
        "coral",
        "turquoise",
        "magenta",
        "black",
        "white"
    ]
}

export default App;
