import React from "react"
import GridDot from "./GridDot"

class Grid extends React.Component{
    constructor(props){
        super(props)
        this.state = {
            selectorIndex: 7
        }
    }

    render(){
        console.log(this.dotArray)
        return(
            <div id="grid">
                {this.dotArray}
            </div>
        )
    }

    dotArray = Array(64).fill("").map((ele, i) => {
        return <GridDot temp={i+1}/>
    }
    )
}

export default Grid
